import React from "react";
import { createUseStyles } from "react-jss";
import mobileDots from "../media/mobileDots.png";

const useStyles = createUseStyles({
  product: {},
  number: {
    position: "relative",
    fontFamily: "Cunia",
    fontSize: 500,
    opacity: 0.36,
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    background:
      "linear-gradient(149deg, rgba(6,255,146,1) -18%, rgba(2,202,246,1) 103%)",
  },
  text: {
    top: "-380px",
    left: "150px",
    position: "relative",
    maxWidth: "466px",
    "& > h3": {
      fontSize: 24,
      fontWeight: 700,
      letterSpacing: 0.2,
    },
    "& > p": {
      fontSize: 18,
      fontWeight: 300,
      lineHeight: 1.
    },
  },
  image: {},
});

export default function Product({ number, title, text }) {
  const classes = useStyles();
  return (
    <div className={classes.product}>
      <div>
        <div className={classes.number}>{number}</div>
        <div className={classes.text}>
          <h3>{title}</h3>
          <p>{text}</p>
        </div>
      </div>
      {/* <img src={mobileDots} alt="mobileDots" className={classes.image} />‹ */}
    </div>
  );
}
